mapper.select:
SELECT B.iuser, B.nm, B.mainimg
  FROM t_dm_user A
 INNER JOIN t_user B
    ON A.iuser = B.iuser
 WHERE A.idm = ${idm}
   AND A.iuser != ${fromiuser}

mapper.insert:
INSERT INTO t_dm_msg
( idm, seq, iuser, msg )
SELECT ${idm}, (IFNULL(MAX(seq), 0) + 1), ${iuser}, #{msg}
  FROM t_dm_msg
 WHERE idm = ${idm}

mapper.select:
SELECT idm
FROM t_dm_user
WHERE iuser = ${fromiuser}
OR iuser = ${toiuser}
GROUP BY idm
HAVING COUNT(idm) = 2
